package src

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestDecodeBits(t *testing.T) {
	tests := []struct {
		bits string
		want string
	}{
		{
			bits: "0000000011011010011100000110000001111110100111110011111100000000000111011111111011111011111000000101100011111100000111110011101100000100000",
			want: "HEY JUDE",
		},
		{
			bits: "1",
			want: "E",
		},
		{
			bits: "1001",
			want: "EE",
		},
		{
			bits: "10111",
			want: "A",
		},
		{
			bits: "1110111",
			want: "M",
		},
		{
			bits: "10000001",
			want: "E E",
		},
		{
			bits: "111",
			want: "E",
		},
		{
			bits: "1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011",
			want: "HEY JUDE",
		},
		{
			bits: "101",
			want: "I",
		},
		{
			bits: "",
			want: "THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG",
		},
		{
			bits: "",
			want: "SOS! THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.",
		},
	}
	for _, tt := range tests {
		t.Run(tt.want, func(t *testing.T) {
			assert.Equal(t, tt.want, DecodeMorse(DecodeBitsAdvanced(tt.bits)))
		})
	}
}
